import { Component } from '@angular/core';

@Component({
  selector: 'app-form-page',
  templateUrl: './form-page.component.html',
  styleUrls: ['./form-page.component.css']
})
export class FormPageComponent {
  customer = {
    name: '',
    email: '',
    phone: ''
};

submitForm(form: any) {
    if (form.invalid) {
        // Perform any additional error handling or display error messages
        return;
    }

    // Submit form logic
    console.log(this.customer);
  }
}
<h2>Customer Details</h2>
<form #customerForm="ngForm" (ngSubmit)="submitForm(customerForm)">
    <div class="mb-3">
        <label for="name">Name:</label>
        <input type="text" id="name" name="name" class="form-control" required [(ngModel)]="customer.name" #name="ngModel">
        <div *ngIf="name.invalid && (name.dirty || name.touched)" class="text-danger">
            <div *ngIf="name.errors.required">Name is required.</div>
        </div>
    </div>

    <div class="mb-3">
        <label for="email">Email:</label>
        <input type="email" id="email" name="email" class="form-control" required [(ngModel)]="customer.email" #email="ngModel">
        <div *ngIf="email.invalid && (email.dirty || email.touched)" class="text-danger">
            <div *ngIf="email.errors.required">Email is required.</div>
            <div *ngIf="email.errors.email">Invalid email format.</div>
        </div>
    </div>

    <div class="mb-3">
        <label for="phone">Phone:</label>
        <input type="tel" id="phone" name="phone" class="form-control" required [(ngModel)]="customer.phone" #phone="ngModel">
        <div *ngIf="phone.invalid && (phone.dirty || phone.touched)" class="text-danger">
            <div *ngIf="phone.er">Phone is required.</div>
        </div>
    </div>

    <button type="submit" class="btn btn-primary" [disabled]="customerForm.invalid">Submit</button>
</form>
