import { Component } from '@angular/core';
import { UserData } from '../user-data';
import { AuthService } from '../auth/auth.service';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-auth-page',
  templateUrl: './auth-page.component.html',
  styleUrls: ['./auth-page.component.css']
})
export class AuthPageComponent {
  user: UserData = {
    name: '',
    email: '',
    password: ''
  };

  constructor(private authService:AuthService, private http:) { }


  onSubmit(formata: NgForm): void {
    // Pass the user object to the AuthService for authentication
    const isLoggedIn = this.authService.login(this.user.email, this.user.password);

    if (isLoggedIn) {
      // Authentication success logic
      console.log('Logged in successfully');
    } else {
      // Authentication error logic
      console.error('Invalid credentials');
    }
  }
}
