import { Component } from '@angular/core';

@Component({
  selector: 'app-form-page',
  templateUrl: './form-page.component.html',
  styleUrls: ['./form-page.component.css']
})
export class FormPageComponent {
  isSuccess = false;
  customer = {
    name: '',
    email: '',
    phone: ''
};

submitForm(form: any) {
    if (form.valid) {
        console.log('Form Submitted', this.customer);
    }
    this.isSuccess = true;
  }
}
